; ---------------------------------------------------------------
; void __near__ draw (unsigned char isMoving)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_draw: near

.segment	"CODE"

;
; {
;
	jsr     pusha
;
; oam_clear();
;
	jsr     _oam_clear
;
; if (isMoving == 1)
;
	ldy     #$00
	lda     (sp),y
	cmp     #$01
	bne     L0002
;
; oam_meta_spr(player_data.x, player_data.y, player_sprite_walk);
;
	jsr     decsp2
	lda     _player_data
	iny
	sta     (sp),y
	lda     _player_data+1
	dey
	sta     (sp),y
	lda     #<(_player_sprite_walk)
	ldx     #>(_player_sprite_walk)
;
; else 
;
	jmp     L0004
;
; oam_meta_spr(player_data.x, player_data.y, player_sprite_idle);
;
L0002:	jsr     decsp2
	lda     _player_data
	iny
	sta     (sp),y
	lda     _player_data+1
	dey
	sta     (sp),y
	lda     #<(_player_sprite_idle)
	ldx     #>(_player_sprite_idle)
L0004:	jsr     _oam_meta_spr
;
; }
;
	jmp     incsp1

.endproc

; ---------------------------------------------------------------
; unsigned char __near__ move (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_move: near

.segment	"CODE"

;
; if (pad1 & PAD_LEFT)
;
	lda     _pad1
	and     #$02
	beq     L0006
;
; player_data.x--;
;
	dec     _player_data
;
; return 1;
;
	ldx     #$00
	lda     #$01
	rts
;
; else if (pad1 & PAD_RIGHT)
;
L0006:	lda     _pad1
	ldx     #$00
	and     #$01
	beq     L0008
;
; player_data.x++;
;
	inc     _player_data
;
; return 1;
;
	lda     #$01
	rts
;
; }
;
L0008:	rts

.endproc

